import React, { useState } from 'react';

const SignInPage = () => {
  const [Name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const HandleNamed = (event) => {
    setName(event.target.value);
  };

  const HandleEmail = (event) => {
    setEmail(event.target.value);
  };

  const HandlePassword = (event) => {
    setPassword(event.target.value);
  };

  const HandleSubmit = (event) => {
    event.preventDefault();
    setEmail('');
    setPassword('');
  };

  return (
    <div className="assign">
      <u><h1 className='header'><i>Sign In</i></h1></u>
      <form onSubmit={HandleSubmit}>
      <br></br>
      <div className="sign-body">
        
      </div>
        <label>
          <input className='Input' type="name" placeholder='Enter your name' id='loginName' value={Name} onChange={HandleNamed} />
        </label>
        <br />
        <label>
          <input className='Input' type="email" placeholder='Enter your e-mail'  id='loginEmail' value={email} onChange={HandleEmail} />
        </label>
        <br />
        <label>
          <input className='Input' type="password" placeholder='Enter your password'  id='loginPAss' value={password} onChange={HandlePassword} />
        </label>
        <br />
        <label>
          <input className='Input' type="password" placeholder='Enter password again'  id='loginPAssAgain' value={password} onChange={HandlePassword} />
        </label>
        <br />
        <button className='Sign-button' type="submit"><a href='/Home'>Login</a></button>
        <button className='Sign-button' type="submit"><a href='/SignUpPage'>Create Account</a></button>
      </form>
    </div>
  );
};

export default SignInPage;
