import React, { useState } from 'react';
import axios from 'axios';

const SignInPage = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleNameChange = (event) => {
    setName(event.target.value);
  };

  const handleEmailChange = (event) => {
    setEmail(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      // Make an API call to the backend using Axios
      const response = await axios.post('http://localhost:3000/SignInPage', {
        name,
        email,
        password,
      });

      // Handle the response, e.g., display a success message or redirect to another page
      console.log('API Response:', response.data);
    } catch (error) {
      // Handle errors, e.g., display an error message to the user
      console.error('Error:', error.message);
    }

    // Clear the form fields
    setName('');
    setEmail('');
    setPassword('');
  };
  function login()
  {
  
      // Send data to the server
      fetch('http://localhost:3000/SignInPage', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      })
        .then(response => response.json())
        .then(data => console.log(data))
        .catch(error => console.error('Error:', error));
    };
  
  return (
    <div className="assign">
      <u><h1 className='header'><i>Sign In</i></h1></u>
      <form onSubmit={handleSubmit}>
        <br></br>
        <div className="sign-body">
        </div>
        <label>
          <input
            className='Input'
            type="text"
            placeholder='Enter your name'
            id='loginName'
            value={name}
            onChange={handleNameChange}
          />
        </label>
        <br />
        <label>
          <input
            className='Input'
            type="email"
            placeholder='Enter your e-mail'
            id='loginEmail'
            value={email}
            onChange={handleEmailChange}
          />
        </label>
        <br />
        <label>
          <input
            className='Input'
            type="password"
            placeholder='Enter your password'
            id='loginPass'
            value={password}
            onChange={handlePasswordChange}
          />
        </label>
        <br />
        <button className='Sign-button' type="button" onClick={login}><a href='/'>login</a></button>
        <button className='Sign-button' type="submit"><a href='/SignUpPage'>Create Account</a></button>
      </form>
    </div>
  );
};

export default SignInPage;
