import React, { useState } from 'react';
import axios from 'axios';

const SignInPage = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [apiResponse, setApiResponse] = useState('');

  const handleNameChange = (event) => {
    setName(event.target.value);
  };

  const handleEmailChange = (event) => {
    setEmail(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      // Make an API call to a public API (JSONPlaceholder in this example)
      const response = await axios.post('https://jsonplaceholder.typicode.com/posts', {
        name,
        email,
        password,
      });

      // Handle the response, e.g., set the response data in state
      setApiResponse(response.data);
    } catch (error) {
      // Handle errors, e.g., display an error message to the user
      console.error('Error:', error.message);
    }

    // Clear the form fields
    setName('');
    setEmail('');
    setPassword('');
  };

  return (
    <div className="assign">
      <u><h1 className='header'><i>Sign In</i></h1></u>
      <form onSubmit={handleSubmit}>
        <br></br>
        <div className="sign-body">
        </div>
        <label>
          <input
            className='Input'
            type="text"
            placeholder='Enter your name'
            id='loginName'
            value={name}
            onChange={handleNameChange}
          />
        </label>
        <br />
        <label>
          <input
            className='Input'
            type="email"
            placeholder='Enter your e-mail'
            id='loginEmail'
            value={email}
            onChange={handleEmailChange}
          />
        </label>
        <br />
        <label>
          <input
            className='Input'
            type="password"
            placeholder='Enter your password'
            id='loginPass'
            value={password}
            onChange={handlePasswordChange}
          />
        </label>
        <br />
        <button className='Sign-button' type="submit">Submit</button>
      </form>

      {/* Display the API response, for demonstration purposes */}
      {apiResponse && (
        <div>
          <h2>API Response:</h2>
          <pre>{JSON.stringify(apiResponse, null, 2)}</pre>
        </div>
      )}
    </div>
  );
};

export default SignInPage;
